import React, { Component } from 'react';
import logo from '../logo.png';
import Web3 from 'web3';
import './App.css';
import Meme from '../abis/Meme.json'

const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'localhost', port: '5001', protocol: 'http' })
//const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https'})

class App extends Component {

  async componentWillMount() {
     await this.loadWeb3()
     await this.loadBlockchainData()
  }


  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    console.log(networkId)
  }

  constructor(props) {
    super(props);
    this.state = {
      account: '',
      buffer: null,
      memeHash: "QmSU9m6q6RxMdMmL1AAmnhHNiiqsj2icy4B5MvssemhiQE"
    };
  }

  async loadWeb3(){
    if (window.ethereum) {
       window.web3 = new Web3(window.ethereum)
       await window.ethereum.enable()
    } if (window.web3) {
       window.web3 = new Web3(window.web3.currentProvider) 
    } else {
      window.alert('Please use metamask')
    }
  }

  captureFile=(event) => {
    event.preventDefault()
    console.log('file captured..')

   //IPFS processing... 
    const file=event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend= () => {
      this.setState({ buffer: Buffer(reader.result) })
    }
  }

  onSubmit = (event) => {
   event.preventDefault()
   console.log("submitting the form...")

   const file=ipfs.add(this.state.buffer, (error,result) => {
     console.log('Ipfs result', result)
     if(error) {
        console.error(error)
        return
     }

   }).then(token => { this.setState({memeHash:token["path"]}) })

  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="http://www.dappuniversity.com/bootcamp"
            target="_blank"
            rel="noopener noreferrer"
          >
            Meme of the Day
          </a>

          <ul className="navbar-nav px-3">
            <li className="nav-item text-nowrap d-none d-sm-none d-sm-block">
              <small className="text-white">{this.state.account}</small>
            </li>
          </ul>


        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={"http://127.0.0.1:8080/ipfs/"+this.state.memeHash} className="App-logo" alt="logo" />
                </a>
                <p>&nbsp;</p>
                <h2>Change Meme</h2>
                <form onSubmit={this.onSubmit} >
                  <input type='file' onChange={this.captureFile}/> 
                  <input type='submit'/>
                  </form>

              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
